from flask import Flask, request, jsonify, render_template, send_from_directory
from openai import OpenAI
import os
import json
import datetime
import re

app = Flask(__name__)
app.secret_key = os.urandom(24)

UPLOAD_FOLDER = 'conversations'
os.makedirs(UPLOAD_FOLDER, exist_ok=True)

client = OpenAI(
    api_key="sk-S8Vm0peLY3QKSXIf8j_ZLw",
    base_url="https://hubai.loe.gg/v1"
)

def sanitize_filename(name):
    """–û—á–∏—â–∞–µ—Ç –∏–º—è —Ñ–∞–π–ª–∞ –æ—Ç —Å–ø–µ—Ü—Å–∏–º–≤–æ–ª–æ–≤"""
    return re.sub(r'[\\/*?:"<>|]', "", name)[:50]

def generate_chat_name(conversation):
    """–ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç –∏–º—è —á–∞—Ç–∞ –Ω–∞ –æ—Å–Ω–æ–≤–µ –ø–µ—Ä–≤–æ–≥–æ —Å–æ–æ–±—â–µ–Ω–∏—è"""
    if not conversation:
        return "–ù–æ–≤—ã–π —á–∞—Ç"

    first_msg = next((msg for msg in conversation if msg["role"] == "user"), None)
    if not first_msg:
        return "–ù–æ–≤—ã–π —á–∞—Ç"

    content = first_msg["content"]
    return sanitize_filename(content[:30].strip() + ("..." if len(content) > 30 else ""))

@app.route('/')
def home():
    try:
        files = []
        for f in os.listdir(UPLOAD_FOLDER):
            if f.endswith('.json'):
                filepath = os.path.join(UPLOAD_FOLDER, f)
                with open(filepath, 'r', encoding='utf-8') as file:
                    try:
                        data = json.load(file)
                        name = data.get('meta', {}).get('name', generate_chat_name(data.get('messages', [])))
                    except:
                        name = "–û—à–∏–±–∫–∞ —á—Ç–µ–Ω–∏—è"

                files.append({
                    'filename': f,
                    'name': name,
                    'date': datetime.datetime.fromtimestamp(os.path.getmtime(filepath)).strftime('%Y-%m-%d %H:%M:%S')
                })

        files.sort(key=lambda x: os.path.getmtime(os.path.join(UPLOAD_FOLDER, x['filename'])), reverse=True)
        return render_template('index.html', conversations=files)
    except Exception as e:
        print(f"Error loading conversations: {str(e)}")
        return render_template('index.html', conversations=[])

@app.route('/api/send_message', methods=['POST'])
def send_message():
    data = request.json
    user_message = data.get('message', '')
    conversation = data.get('messages', [])

    if not user_message:
        return jsonify({'error': 'Empty message'}), 400

    try:
        messages = [{"role": msg["role"], "content": msg["content"]} for msg in conversation]
        messages.append({"role": "user", "content": user_message})

        response = client.chat.completions.create(
            model="deepseek-chat",
            messages=messages,
            temperature=0.7,
            max_tokens=12000
        )

        assistant_reply = response.choices[0].message.content
        new_messages = conversation + [
            {"role": "user", "content": user_message, "timestamp": datetime.datetime.now().isoformat()},
            {"role": "assistant", "content": assistant_reply, "timestamp": datetime.datetime.now().isoformat()}
        ]

        return jsonify({
            'status': 'success',
            'assistant_reply': assistant_reply,
            'messages': new_messages
        })

    except Exception as e:
        return jsonify({
            'status': 'error',
            'error': str(e),
            'messages': conversation
        }), 500

@app.route('/api/save_conversation', methods=['POST'])
def save_conversation():
    data = request.json
    messages = data.get('messages', [])
    custom_name = data.get('name', '')

    if not messages:
        return jsonify({'error': 'Empty conversation'}), 400

    try:
        timestamp = datetime.datetime.now().strftime("%Y%m%d_%H%M%S")
        name = sanitize_filename(custom_name) if custom_name else generate_chat_name(messages)
        filename = f"conv_{timestamp}_{name}.json"
        filepath = os.path.join(UPLOAD_FOLDER, filename)

        with open(filepath, 'w', encoding='utf-8') as f:
            json.dump({
                'meta': {
                    'name': name,
                    'created': datetime.datetime.now().isoformat()
                },
                'messages': messages
            }, f, indent=2, ensure_ascii=False)

        return jsonify({
            'status': 'success',
            'filename': filename,
            'name': name
        })
    except Exception as e:
        return jsonify({
            'status': 'error',
            'error': str(e)
        }), 500

@app.route('/api/rename_chat', methods=['POST'])
def rename_chat():
    data = request.json
    old_filename = data.get('filename')
    new_name = sanitize_filename(data.get('new_name'))

    if not all([old_filename, new_name]):
        return jsonify({'error': 'Missing parameters'}), 400

    try:
        old_path = os.path.join(UPLOAD_FOLDER, old_filename)
        if not os.path.exists(old_path):
            return jsonify({'error': 'File not found'}), 404

        with open(old_path, 'r', encoding='utf-8') as f:
            chat_data = json.load(f)

        chat_data['meta']['name'] = new_name

        # –°–æ–∑–¥–∞–µ–º –Ω–æ–≤–æ–µ –∏–º—è —Ñ–∞–π–ª–∞ —Å —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ–º timestamp
        parts = old_filename.split('_')
        if len(parts) >= 3:
            new_filename = f"{parts[0]}_{parts[1]}_{new_name}.json"
        else:
            new_filename = f"conv_{datetime.datetime.now().strftime('%Y%m%d_%H%M%S')}_{new_name}.json"

        new_path = os.path.join(UPLOAD_FOLDER, new_filename)

        with open(new_path, 'w', encoding='utf-8') as f:
            json.dump(chat_data, f, indent=2, ensure_ascii=False)

        os.remove(old_path)

        return jsonify({
            'status': 'success',
            'new_filename': new_filename,
            'new_name': new_name
        })
    except Exception as e:
        return jsonify({
            'status': 'error',
            'error': str(e)
        }), 500

@app.route('/api/delete_chat', methods=['POST'])
def delete_chat():
    data = request.json
    filename = data.get('filename')

    if not filename:
        return jsonify({'error': 'Missing filename'}), 400

    try:
        filepath = os.path.join(UPLOAD_FOLDER, filename)
        if not os.path.exists(filepath):
            return jsonify({'error': 'File not found'}), 404

        os.remove(filepath)
        return jsonify({'status': 'success'})
    except Exception as e:
        return jsonify({
            'status': 'error',
            'error': str(e)
        }), 500

@app.route('/api/load_conversation/<filename>', methods=['GET'])
def load_conversation(filename):
    try:
        if not filename.endswith('.json') or '/' in filename or '\\' in filename:
            return jsonify({'error': 'Invalid filename'}), 400

        filepath = os.path.join(UPLOAD_FOLDER, filename)

        if not os.path.exists(filepath):
            return jsonify({'error': 'File not found'}), 404

        with open(filepath, 'r', encoding='utf-8') as f:
            data = json.load(f)

        return jsonify({
            'status': 'success',
            'messages': data.get('messages', []),
            'name': data.get('meta', {}).get('name', ''),
            'filename': filename
        })
    except Exception as e:
        return jsonify({
            'status': 'error',
            'error': str(e)
        }), 500

if __name__ == '__main__':
    app.run(debug=True, port=5000)
