                        updateConversationList();
                        highlightCurrentConversation();
                    } else {
                        alert(`–û—à–∏–±–∫–∞ —Å–æ—Ö—Ä–∞–Ω–µ–Ω–∏—è: ${data.error || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞'}`);
                    }
                } catch (error) {
                    alert(`–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è: ${error.message}`);
                }
            }

            async function updateConversationList() {
                try {
                    const response = await fetch('/');
                    const parser = new DOMParser();
                    const htmlDoc = parser.parseFromString(await response.text(), 'text/html');
                    const newList = htmlDoc.getElementById('conversation-list');
                    conversationList.innerHTML = newList.innerHTML;

                    // –ü–µ—Ä–µ–ø—Ä–∏–≤—è–∑—ã–≤–∞–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π
                    document.querySelectorAll('.conversation-item').forEach(item => {
                        item.addEventListener('click', () => loadConversation(item.dataset.filename));
                    });

                    document.querySelectorAll('.rename-btn').forEach(btn => {
                        btn.addEventListener('click', (e) => {
                            e.stopPropagation();
                            const item = btn.closest('.conversation-item');
                            showRenameModal(item.dataset.filename, item.querySelector('.conversation-name').textContent);
                        });
                    });

                    document.querySelectorAll('.delete-btn').forEach(btn => {
                        btn.addEventListener('click', (e) => {
                            e.stopPropagation();
                            const item = btn.closest('.conversation-item');
                            showDeleteModal(item.dataset.filename);
                        });
                    });
                } catch (error) {
                    console.error('–û—à–∏–±–∫–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è —Å–ø–∏—Å–∫–∞:', error);
                }
            }

            async function loadConversation(filename) {
                try {
                    const response = await fetch(`/api/load_conversation/${filename}`);

                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }

                    const data = await response.json();

                    if (data.status === 'success') {
                        conversation = [];
                        chatContainer.innerHTML = '';
                        currentFilename = data.filename;
                        currentChatName = data.name;

                        data.messages.forEach(msg => {
                            addMessage(msg.role, msg.content, msg.timestamp);
                        });

                        highlightCurrentConversation();
                    } else {
                        alert(`–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —á–∞—Ç–∞: ${data.error || '–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –æ—à–∏–±–∫–∞'}`);
                    }
                } catch (error) {
                    alert(`–û—à–∏–±–∫–∞ —Å–æ–µ–¥–∏–Ω–µ–Ω–∏—è: ${error.message}`);
                }
            }

            function highlightCurrentConversation() {
                document.querySelectorAll('.conversation-item').forEach(item => {
                    if (item.dataset.filename === currentFilename) {
                        item.classList.add('active');
                    } else {
                        item.classList.remove('active');
                    }
                });
            }

            // –§—É–Ω–∫—Ü–∏–∏ –¥–ª—è —Ä–∞–±–æ—Ç—ã —Å –º–æ–¥–∞–ª—å–Ω—ã–º–∏ –æ–∫–Ω–∞–º–∏
            function showRenameModal(filename, currentName) {
                fileToRename = filename;
                newChatNameInput.value = currentName;
                renameModal.style.display = 'flex';
                newChatNameInput.focus();
            }

            function showDeleteModal(filename) {
                fileToDelete = filename;
                deleteModal.style.display = 'flex';
            }

            async function renameChat() {
                if (!fileToRename || !newChatNameInput.value.trim()) return;

                try {
                    const response = await fetch('/api/rename_chat', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({
                            filename: fileToRename,
                            new_name: newChatNameInput.value.trim()
                        })
                    });

                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }

                    const data = await response.json();

                    if (data.status === 'success') {
                        if (currentFilename === fileToRename) {
                            currentFilename = data.new_filename;
                            currentChatName = data.new_name;
                        }
                        updateConversationList();
                    }
                } catch (error) {
                    alert(`–û—à–∏–±–∫–∞ –ø–µ—Ä–µ–∏–º–µ–Ω–æ–≤–∞–Ω–∏—è: ${error.message}`);
                } finally {
                    renameModal.style.display = 'none';
                    fileToRename = null;
                }
            }

            async function deleteChat() {
                if (!fileToDelete) return;

                try {
                    const response = await fetch('/api/delete_chat', {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({
                            filename: fileToDelete
                        })
                    });

                    if (!response.ok) {
                        throw new Error(`HTTP error! status: ${response.status}`);
                    }

                    const data = await response.json();

                    if (data.status === 'success') {
                        if (currentFilename === fileToDelete) {
                            currentFilename = null;
                            currentChatName = '';
                            conversation = [];
                            chatContainer.innerHTML = '';
                        }
                        updateConversationList();
                    }
                } catch (error) {
                    alert(`–û—à–∏–±–∫–∞ —É–¥–∞–ª–µ–Ω–∏—è: ${error.message}`);
                } finally {
                    deleteModal.style.display = 'none';
                    fileToDelete = null;
                }
            }

            // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
            function init() {
                // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π
                sendButton.addEventListener('click', sendMessage);
                userInput.addEventListener('keydown', function(e) {
                    if (e.key === 'Enter' && !e.shiftKey) {
                        e.preventDefault();
                        sendMessage();
                    }
                });

                newChatButton.addEventListener('click', newChat);
                saveChatButton.addEventListener('click', saveChat);

                // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –¥–ª—è —Å–ø–∏—Å–∫–∞ —á–∞—Ç–æ–≤
                document.querySelectorAll('.conversation-item').forEach(item => {
                    item.addEventListener('click', () => loadConversation(item.dataset.filename));
                });

                document.querySelectorAll('.rename-btn').forEach(btn => {
                    btn.addEventListener('click', (e) => {
                        e.stopPropagation();
                        const item = btn.closest('.conversation-item');
                        showRenameModal(item.dataset.filename, item.querySelector('.conversation-name').textContent);
                    });
                });

                document.querySelectorAll('.delete-btn').forEach(btn => {
                    btn.addEventListener('click', (e) => {
                        e.stopPropagation();
                        const item = btn.closest('.conversation-item');
                        showDeleteModal(item.dataset.filename);
                    });
                });

                // –û–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ –º–æ–¥–∞–ª—å–Ω—ã—Ö –æ–∫–æ–Ω
                document.getElementById('cancel-rename').addEventListener('click', () => {
                    renameModal.style.display = 'none';
                });

                confirmRenameBtn.addEventListener('click', renameChat);
                newChatNameInput.addEventListener('keypress', (e) => {
                    if (e.key === 'Enter') renameChat();
                });

                document.getElementById('cancel-delete').addEventListener('click', () => {
                    deleteModal.style.display = 'none';
                });

                confirmDeleteBtn.addEventListener('click', deleteChat);

                // –ó–∞–∫—Ä—ã—Ç–∏–µ –º–æ–¥–∞–ª—å–Ω—ã—Ö –æ–∫–æ–Ω –ø–æ –∫–ª–∏–∫—É –≤–Ω–µ –∏—Ö
                window.addEventListener('click', (e) => {
                    if (e.target === renameModal) {
                        renameModal.style.display = 'none';
                    }
                    if (e.target === deleteModal) {
                        deleteModal.style.display = 'none';
                    }
                });
            }

            init();
        });
    </script>
</body>
</html>
